mapper.insert:

	select nvl(max(qreply_num),0)+1 from qreply

insert into qreply(qreply_num, qreply_content, qreply_date, id, qna_num)
values(#{qreply_num}, #{qreply_content}, sysdate, #{id},#{qna_num}
)

mapper.update:
update qreply
set qreply_content = #{qreply_content}
where qreply_num = #{qreply_num}

mapper.select:
		select qreply_num, qreply_content, to_char(qreply_date,'yyyy-mm-dd') qreply_date, id,
		qna_num, r
		FROM(
		select qreply_num, qreply_content, qreply_date, id, qna_num, rownum r
		FROM(
		select qreply_num, qreply_content, qreply_date, id, qna_num
		from QREPLY
		where qna_num = #{qna_num}
		order by qreply_num DESC
		)
		)

WHERE r >= #{sno} and r <= #{eno}
