mapper.insert:
insert into qna(qna_num, qna_title, qna_content, qna_date, qna_type, qna_pw,
qna_grpno, id)
values((select nvl(Max(qna_num),0)+1 as qna_num
from qna),
#{qna_title},#{qna_content}, sysdate, #{qna_type}, (select nvl(Max(qna_grpno),0)+1 as qna_grpno
from qna), #{id})

mapper.update:
update qna
set qna_title=#{qna_title}, qna_content=#{qna_content},
qna_type=#{qna_type}
where qna_num= #{qna_num}

mapper.select:
select qna_num, qna_grpno, qna_title, qna_indent, qna_ansnum
from qna
where qna_num = #{qna_num}

mapper.update:
update qna
set qna_ansnum = qna_ansnum +1
where qna_grpno = #{qna_grpno}
and qna_ansnum > #{qna_ansnum}
